package GroupC_Operators;

public class Arithmetic_operators1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a, b, c, d;  
		
		a=20;
		b=5;
		c=10;
		d=a+b+c;
		
		System.out.println("Add: " +d);
		
		d = b-a;
		
		System.out.println("Subtract: " +d);
		
		c = a*b;
		
		System.out.println("multiply: " +c);
		
		d = c/a;
		
		System.out.println("Division: " +d);
		
		d=c%b;
		
		System.out.println("remainder: " +d);
		
		c= --c;//99
		
		System.out.println("Decrement: " +c);	
		
		c= c++; 
		
		System.out.println("Increment: " +c); //100
		
		c= --c; //98
		
		System.out.println("Increment: " +c);
		
		c= c++; //98
		
		System.out.println("Increment: " +c);
		
		c= c++; 
		
		System.out.println("Increment: " +c);
		
        c= c++; 
		
		System.out.println("Increment: " +c);
		
		
		
		
		
		
		
		
		
		
		
	}

}
