package GroupC_Operators;

public class Logical_operators1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		boolean a = true;
		boolean b = false;
		
		
		System.out.println("out put result: " + (a&&b)); //false
		System.out.println("out put result: " + (a||b)); //true
		
		System.out.println("out put result: " + !(a&&b));// true the result will be the opposite
		
		
		

	}

}
