package GroupC_Operators;

public class Ternary_Operators1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a,b,c;
		
		a = 30;
		b = 20;
		c = (a==1)? 25: 55;
		
		System.out.println("value of c: "+ c);
		
		c = (b==20)? 25: 60;
		
		System.out.println("value of c: "+ c);
		
	}

}
