package switchDemo;

public class String_Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String dog = "bull dog";
		
		switch(dog) {
			case "chewawa":
				System.out.println("small dog");
				break;
			case "bull dog":
				System.out.println("large dog");
				break;
			default:
				System.out.println("try a different dog name");
			
				
		
		}
		

	}

}
